import React from 'react'
import {View, FlatList, Text, ScrollView, Image, StyleSheet, SafeAreaView, KeyboardAvoidingView, TouchableOpacity} from 'react-native'
import SizedBox from './components/SizedBox';

const FavouriteScreen = (props) => {

  const DATA = [
    {
      id: 'bd7acbea-c1b1-46c2-aed5-3ad53abb28ba',
      title: 'First Item',
    },
    {
      id: '3ac68afc-c605-48d3-a4f8-fbd91aa97f63',
      title: 'Second Item',
    },
    {
      id: '58694a0f-3da1-471f-bd96-145571e29d72',
      title: 'Third Item',
    },
  ];

  const Item = ({ title }) => (
    <View style={styles.item}>
      <Text style={styles.titlelist}>{title}</Text>
    </View>
  );


    const renderItem = ({ item }) => (
      <Item title={item.title} />
    );

  return (
    <ScrollView style={styles.root}>
    <View style={styles.container}>
    <Image style={styles.logostyle} source={require('../../assets/B2.png')} />
    </View>

    <SafeAreaView style={styles.safeAreaView}>
        <KeyboardAvoidingView
          behavior={Platform.OS === 'ios' ? 'padding' : 'height'}
          style={styles.content}
        >

          <Text style={styles.title}>Your Favourite Journey is Here!</Text>
          <SizedBox height={8} />

          <Text style={styles.subtitle}>Choose your journey to directly ride the bus</Text>
          <SizedBox height={32} />
          </KeyboardAvoidingView>
<View>
<FlatList
        data={DATA}
        renderItem={renderItem}
        keyExtractor={item => item.id}
      />
</View>
          </SafeAreaView>
    </ScrollView>

  )}

  const styles = StyleSheet.create({
    container: {

      justifyContent: 'center', //Centered vertically
      alignItems: 'center', // Centered horizontally
      flex: 1,
      marginTop: 70,
      marginBottom: 40
      // height: '100%'

    },
    logostyle: {

      width: 120,
      height: 40,
      textAlign: 'center'

    },
    root: {
      backgroundColor: "#D2DCFF",
      flex: 1,

    },
    content: {
      flex: 1,
      justifyContent: 'center',
      paddingHorizontal: 16,
      paddingVertical: 32,
    },
    subtitle: {
      // color: 'rgba(235, 235, 245, 0.6)',
      color: "#394A48",
      fontSize: 17,
      fontWeight: '400',
      lineHeight: 22,
    },
    title: {
      color: "#394A48",
      fontSize: 28,
      fontWeight: '700',
      lineHeight: 34,
    },
    safeAreaView: {
      flex: 1,
    },
    item: {
      backgroundColor: 'white',
      padding: 20,
      marginVertical: 8,
      marginHorizontal: 16,

    },
    titlelist: {
      fontSize: 16,
    },

  })

export default FavouriteScreen;
