import React from 'react';
import {View, Text,  Image, TouchableOpacity, StyleSheet, Pressable, TextInput} from 'react-native'
import SizedBox from './components/SizedBox';
import { useForm, Controller } from "react-hook-form";

const ProfileScreen = () => {
  const {control, handleSubmit, formState: {errors}, watch} = useForm ({
    defaultValues: {
      username: '',
      email: '',
      password: '',
      password_repeat: ''
    }
}) 
const onSubmit = handleSubmit(({username, email, password, password_repeat}) => {
  Alert.alert('Data', ` Username: ${username}\nEmail : ${email}\nPassword: ${password}\nRepeat Password ${password_repeat}`)
})

  return (

    <View style={styles.container}>
    <View style={styles.header}></View>
    <Image style={styles.avatar} source={{uri: 'https://bootdey.com/img/Content/avatar/avatar6.png'}}/>
    <View style={styles.body}>

    <View style={styles.bodyContent}>
      <Text style={styles.name}>Ilyana Norzali</Text>
              <Text style={styles.info}>UX Designer / Mobile developer</Text>
              <Text style={styles.description}>Lorem ipsum dolor sit amet, saepe sapientem eu nam. Qui ne assum electram expetendis, omittam deseruisse consequuntur ius an,</Text>





        <TouchableOpacity style={styles.savebutton}>
          <Text style={styles.buttonTitle} >Save Changes</Text>  
        </TouchableOpacity>              


      </View>
  </View>
</View>
  )
}

const styles = StyleSheet.create({
  header:{
    backgroundColor: "#D2DCFF",
    height:150,
  },
  avatar: {
    width: 130,
    height: 130,
    borderRadius: 63,
    borderWidth: 4,
    borderColor: "white",
    marginBottom:10,
    alignSelf:'center',
    position: 'absolute',
    marginTop:80
  },
  name:{
    fontSize: 22,
    color:"#FFFFFF",
    fontWeight:'600',
    justifyContent: 'center',
    marginTop: 20
  },
  body:{
    marginTop:40,
  },
  bodyContent: {
    flex: 0,
    alignItems: 'center',
    padding:30,
  },
  name:{
    fontSize:28,
    color: "#696969",
    fontWeight: "600"
  },
  info:{
    fontSize:16,
    color: "#00BFFF",
    marginTop:10
  },
  description:{
    fontSize:16,
    color: "#696969",
    marginTop:10,
    textAlign: 'center'
  },
  savebutton: {
    marginTop:10,
    height:48,
    justifyContent: 'center',
    alignItems: 'center',
    marginBottom:20,
    width: "100%",
    borderRadius:0,
    backgroundColor: "#2E3035", },

    buttonTitle: {
      color: '#FFFFFF',
      fontSize: 17,
      fontWeight: '600',
      lineHeight: 22,
    }
  })
export default ProfileScreen
