import React, { useCallback, useMemo, useRef } from 'react';
import { View, Text, StyleSheet, Image, TouchableOpacity, } from 'react-native';


import { api } from './api/api';

import { useSafeAreaInsets } from 'react-native-safe-area-context';
import { useNavigation } from '@react-navigation/native';
import { BottomSheetFlatList } from '@gorhom/bottom-sheet';


const BusStopScreen = () => {
  const {bottom}= useSafeAreaInsets();
   const navigation = useNavigation();


  const snap1 = [
    '17%','40%', '65%'
  ]
    const handleRoute = () => {
        navigation.navigate('ETA')
    }

    const renderItem = useCallback(
      ({item}) => (

        <View style ={styles.innerstyle}>
          <View style={{flexDirection: "column", marginRight: 10}}>

          <Image 
            style = {styles.bustopstyle}
            source = {require("../../assets/busstop.png")}/>
            <Image 
            style = {styles.busstyle}
            source = {require("../../assets/bus.png")}/>
          </View>

            <View style={{flexDirection: "column", width: 200, height: 100}}>

            <Text style = {styles.stationStyle}>{item.bus_station}</Text>
            <Text></Text>
            <Text style = {styles.busnoStyle} >{item.bus_no}</Text>
            <Text style = {styles.busnameStyle}>{item.bus_name}</Text>




            </View>
            <View style={styles.columnStyle}>
            <Text style = {styles.walkStyle} >{item.bus_walk}</Text>
              <Text style = {styles.timetextStyle} >{item.bus_time}</Text>

              {/* <TouchableOpacity  
              onPress = {() => handleRoute() } 
              style={{marginTop: 12}} >

            <AntDesign name="right" size={24} color="black" />
            </TouchableOpacity> */}

            </View>


        </View>




      )

    )


    return (
      <>
      <Text style={{ color: "#394A48", fontSize: 18, fontWeight: 'bold', paddingBottom: 10, textAlign: 'center', backgroundColor: 'white' }}>BUS STOP NEAR ME</Text>
      <BottomSheetFlatList
        data={api}
        keyExtractor={i => i.id}
        renderItem={renderItem}
        contentContainerStyle={{paddingBottom: "bottom" , backgroundColor: 'white' }}


      />
    </>
    )
  }




  const styles = StyleSheet.create({
    // container: {
    //   flex: 1,
    //   backgroundColor: 'grey',
    // },
    // contentContainer: {
    //   flex: 1,
    //   alignItems: 'center',
    // },
    // title:{
    //   fontSize: 16
    // }
    busstyle:{
      width: 40,
      height: 35,
      marginRight: 0,
      marginLeft: 0,
      marginTop: 10
    },
    bustopstyle:{
      width: 40,
      height: 45,
      marginRight: 10,
      marginLeft: 0

    },
    innerstyle: {
      width: 350,
      flex: 1,
      borderWidth: 0,
      paddingBottom: 20,
      // borderColor: "red",
      marginTop: 30,
      flexDirection: "row",
      justifyContent: "space-evenly",
      alignItems: "center",
      borderBottomColor: "#decfce",
    borderBottomWidth: 0.25


    },
    busnoStyle:{

      fontWeight: "bold",
      color: "#394A48",
      marginTop: 7
    },
    busnameStyle:{

      // fontWeight: "bold",
      color: "#394A48",
      fontSize: 12

    },
    timetextStyle:{

      fontWeight: "bold",
      color: "#FF6A15",
      marginTop: 15,
      fontSize:12


    },
    walkStyle:{

      fontWeight: "bold",
      color: "#5ebf49",
      marginTop: 15,
      fontSize:12,
      marginBottom: 20


    },
    columnStyle: {
      flex: 1, 
      flexDirection: 'column',
      marginLeft: 5,
      borderWidth: 0
    },
    stationStyle:{
      fontWeight: "bold",
      color: "#394A48",
      marginTop: 7
    }
  });
  export default BusStopScreen;
