import React, { useCallback, useMemo, useRef } from 'react';
import { View, Text, StyleSheet, Image, TouchableOpacity } from 'react-native';
import { api } from './api/api';
import { AntDesign } from '@expo/vector-icons';
import { BottomSheetFlatList } from '@gorhom/bottom-sheet';
import { useSafeAreaInsets } from 'react-native-safe-area-context';
import { useNavigation } from '@react-navigation/native';






const BusScreen = () => {

   const {bottom}= useSafeAreaInsets();
   const navigation = useNavigation();

  const handleRoute = () => {

      navigation.navigate('ETA')
  }

  const renderItem = useCallback(
    ({ item }) => (
      < View style={styles.innerstyle}>

        <View style={{ marginLeft: 10 }}>
          <Image
            style={styles.busstyle}
            source={require("../../assets/bus.png")} />
        </View>
        <View style={{ flexDirection: "column", width: 200, height: 40 }}>
          <Text style={styles.textStyle} >{item.bus_no}</Text>
          <Text style={styles.placetextStyle}>{item.bus_name}</Text>

        </View>
        <View style={styles.columnStyle}>

          <Text style={styles.timetextStyle} >{item.bus_time}</Text>
          <TouchableOpacity
            onPress={() => handleRoute()}
            style={{ marginTop: 12 }} >

            <AntDesign name="right" size={24} color="black" />
          </TouchableOpacity>

        </View>

      </View>

    ),


  )


  return (
    <>
      {/* <Maps /> */}
      {/* <Bottom data={api} title={title} handleRoute={handleRoute} renderItem = {renderItem} snap={snap1}/> */}
      <Text style={{ color: "#394A48", fontSize: 18, fontWeight: 'bold', paddingBottom: 10, textAlign: 'center', backgroundColor: 'white' }}>BUS NEAR ME</Text>
      <BottomSheetFlatList
        data={api}
        keyExtractor={i => i.id}
        renderItem={renderItem}
        contentContainerStyle={{paddingBottom: bottom,backgroundColor: 'white' }}


      />
    </>
  )
}
const styles = StyleSheet.create({

  busstyle: {
    width: 40,
    height: 40,
    marginRight: 10,
    marginLeft: 15,

  },
  innerstyle: {
    flex: 1,
    width: 400,
    borderWidth: 0,
    paddingBottom: 20,
    paddingTop: 3,
    marginTop: 30,
    flexDirection: "row",
    justifyContent: "space-evenly",
    alignItems: "center",
    borderBottomColor: "#decfce",
    borderBottomWidth: 0.25,




  },
  textStyle: {

    fontWeight: "bold",
    color: "#394A48"
  },
  placetextStyle: {

    // fontWeight: "bold",
    color: "#394A48",
    // fontSize: 12,
    marginTop: 5

  },
  timetextStyle: {

    fontWeight: "bold",
    color: "#FF6A15",
    marginTop: 15,



  },
  columnStyle: {
    flex: 1,
    flexDirection: 'row',
    marginLeft: 5,
  },
});
export default BusScreen;
