import React, { useState, useLayoutEffect, useRef } from 'react'
import Timeline from 'react-native-timeline-flatlist'
import {View, StyleSheet} from 'react-native'



const VerticalWrapper = () => {
  const data = [
    {time: '09:00', title: 'Taman Jasa Utama', description: '', circleColor: '#009688',lineColor:'#009688'},
      {time: '10:45', title: 'Bandar Baru Selayang Fasa 2 (OPP)', description: 'Jalan Sungai Tua'},
      {time: '12:00', title: 'Kampung Bendahara', description: 'Jalan Sungai Tua'},
      {time: '14:00', title: 'Kilang Papan Kg Bendahara', description: 'Jalan Sungai Tua '},
      {time: '16:30', title: 'Taman Jasa', description: 'Jalan Sungai Tua'},
	  {time: '09:00', title: 'Kg Sungai Tua', description: 'Jalan Sungai Tua '},
      {time: '10:45', title: 'Simpang Tiga Nakhoda', description: 'Jalan Sungai Tua'},
      {time: '12:00', title: 'SL72 Masjid Kg Nakhoda',description: 'Jln Nakhoda Kanan' },
      {time: '14:00', title: 'Simpang', description: 'Jln Nakhoda Kanan ',lineColor:'#009688'},



	]
  return (
    <>
      <Timeline 
          style={styles.list}
          data={data}
          circleSize={35}
          dotSize={18}
          circleColor='#cc7621'
          lineColor='#cc7621'
          timeContainerStyle={{minWidth:52}}
          timeStyle={{textAlign: 'center', backgroundColor:'#2E3035', color:'white', padding:5, borderRadius:10}}
          descriptionStyle={{color:'gray'}}
          options={{
            style:{paddingTop:5}
          }}
          innerCircle={'dot'}
        />
    </>
  )
}
const styles = StyleSheet.create({
	container: {
	  flex: 1,
	  padding: 20,
		  paddingTop:65,
	  backgroundColor:'white'
	},
	list: {
	  flex: 1,
	  marginTop:30,
    marginLeft: 10
	},
})
export default VerticalWrapper
