import React, { useState, useRef, useEffect } from 'react';
import { View, Text, Image, Button, StyleSheet, TouchableOpacity, FlatList, Platform } from "react-native"

import { useFonts, Roboto_300Light } from '@expo-google-fonts/roboto'
import Searchbar from './components/Searchbar'
import { SafeAreaView } from 'react-native';
import { useNavigation } from '@react-navigation/native';
import * as Location from 'expo-location';
import { selectUser } from './slices/userSlice';
import { useSelector } from 'react-redux';






const HomeScreen = () => {
  const navigation = useNavigation();
  // const [test, setTest] = useState(true);
  const [location, setLocation] = useState();
  const [error, setError] = useState();
  const {username} = useSelector(selectUser)

  useEffect(() => {
    (async () => {
      if (Platform.OS === 'android' && !Constants.isDevice) {
        setErrorMsg(
          'Oops, this will not work on Snack in an Android emulator. Try it on your device!'
        );
        return;
      }
      let { status } = await Location.requestForegroundPermissionsAsync();
      if (status !== 'granted') {
        setErrorMsg('Permission to access location was denied');
        return;
      }

      let location = await Location.getCurrentPositionAsync({});
      setLocation(location);
      return

    })();
  }, []);




  // const ref = useRef();


  // useEffect(() => {
  //   setTest(ref.current?.isFocused(false));


  // }, [test]);
  // console.log(test)


  const [fontsLoaded] = useFonts({
    Roboto_300Light,
  });

  const buscard = [
    {
      "id": "bd7acbea-c1b1-46c2-aed5-3ad53abb28ba",
      "title": "BUS",
      "content": <Text>Find your nearest bus</Text>,
      "picture": "https://d3avoj45mekucs.cloudfront.net/rojakdaily/media/desiree-gasper/rapid-kl_1.jpg?ext=.jpg"
    },
    {
      "id": "3ac68afc-c605-48d3-a4f8-fbd91aa97f63",
      "title": "BUS STOP",
      "content": <Text>Find your nearest bus stop</Text>,
      "picture": "https://edotcogroup.com/wp-content/uploads/2019/09/smart-bus-stop-project-04.jpg"
    },
  ]


  const handlePress = (item) => {

    console.log(item)

    if (item === 'bd7acbea-c1b1-46c2-aed5-3ad53abb28ba') {
      navigation.navigate('Map', {
        title: "BUS NEAR ME",
        initialRouteName: 'Bus'
      })
    } else if (item === '3ac68afc-c605-48d3-a4f8-fbd91aa97f63') {
      navigation.navigate('Map', {
        title: "BUS STOP NEAR ME",
        initialRouteName: 'Busstop'
      })

    }

  }
  return (

    <SafeAreaView style={styles.root}>





      <Text style={styles.hellostyle}>
        Hello,
      </Text>
      <Text style={styles.textstyle}>
        where would you like to
      </Text>
      <Text style={styles.textstyle}>
        go today?
      </Text>

      <Searchbar

      />
      {/* {test ? (
        <>

        </>
      ) : null} */}
      <Text style={styles.nearstyle}> NEAR YOU </Text>
      <FlatList

        showsHorizontalScrollIndicator="false"
        horizontal
        data={buscard}
        keyExtractor={(buscard) => buscard.id}
        renderItem={({ item }) => {
          return (
            <TouchableOpacity onPress={() => handlePress(item.id)}>
              <View style={styles.container}>
                <Image style={styles.image} source={{ uri: item.picture }} />


                <View style={styles.textContainer}>
                  <Text style={styles.text}>
                    {item.title}
                  </Text>
                  <Text style={styles.text1}>
                    {item.content}
                  </Text>
                </View>
              </View>
            </TouchableOpacity>
          )
        }}

      />


    </SafeAreaView>

  )
}


const styles = StyleSheet.create({
  // backgroundstyle: {
  //   backgroundColor: "#C3FFF8",
  //   flex: 1

  // },
  root: {
    backgroundColor: "#D2DCFF",
    flex: 1,

  },
  logostyle: {
    width: 150,
    height: 50,
    marginLeft: 15,
    marginTop: 10
  },
  hellostyle: {
    marginTop: 100,
    fontSize: 30,
    fontWeight: "bold",
    // fontFamily: "Roboto_300Light",
    marginLeft: 15,
    flexDirection: "row",
    color: "#394A48"
  },
  textstyle: {
    fontSize: 25,
    fontWeight: "bold",
    // fontFamily: "Roboto_300Light",
    marginLeft: 15,
    flexDirection: "row",
    color: "#394A48"
  },
  // circle1: {
