  //   width: 400,
  //   height: 370,
  //   borderRadius: 200,
  //   left: -150,
  //   top: -100,
  //   backgroundColor: "#47DECC",
  // },
  // circle2: {
  //   position: 'absolute',
  //   width: 500,
  //   height: 470,
  //   borderRadius: 200,
  //   top: 400,
  //   left: -45,
  //   backgroundColor: "#47DECC",
  // },


  nearstyle: {
    textAlign: 'center',
    marginTop: 50,

    marginBottom: 30,
    fontSize: 16,
    fontWeight: "bold",
    color: "#394A48"
  },
  buttonstyle: {
    marginTop: 20
  },
  // imagestyle: {
  //   width: 250,
  //   height: 150,
  //   borderRadius: 10,
  //   margin: 10,

  //   resizeMode: 'cover'



  // },
  container: {
    width: 300,
    height: 210,
    marginBottom: 25,
    borderRadius: 10,
    backgroundColor: '#FFFFFF',
    overflow: 'hidden',

    marginLeft: 15,


  },
  textContainer: {
    flex: 1,
    justifyContent: 'flex-start',
    marginTop: 10,
    marginLeft: 10,

  },
  image: {
    width: 300,
    height: 160
  },
  text: {
    fontWeight: 'bold',
    fontSize: 14,
    color: "#394A48"
  },
  text1: {

    fontSize: 12,
    color: "#93A3A1"
  }

})


export default HomeScreen;
